sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	if(g_form.getValue('state')==3 || g_form.getValue('state')==4){
		g_form.setReadOnly('channel',true);
		g_form.setReadOnly('assignment_group',true);
		g_form.setReadOnly('assigned_to',true);
		g_form.setReadOnly('short_description',true);
		g_form.setReadOnly('desc',true);
		g_form.setReadOnly('impact',true);
		g_form.setReadOnly('urgency',true);
		g_form.setReadOnly('priority',true);
		g_form.setReadOnly('vendor_name',true);
		g_form.setReadOnly('vendor_email',true);
		g_form.setReadOnly('vendor_number',true);
		g_form.setReadOnly('suspect_name',true);
		g_form.setReadOnly('suspect_address',true);
		g_form.setReadOnly('suspect_email',true);
		g_form.setReadOnly('suspect_number',true);
	}

}
